import { AppBar, Box, Modal, Toolbar, Typography } from '@material-ui/core';
import React, { useEffect, useState } from 'react';
import './App.css';
import EmissionsResults from './components/EmissionsResults/EmissionsResults';
import Form from './components/Form/Form';

const modalStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '80%',
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

function App() {
  const [emissionsData, setEmissionsData] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const handleOpenModal = () => setIsModalOpen(true);
  const handleCloseModal = () => setIsModalOpen(false);

  const getEmissionsData = (emissions) => {
    setEmissionsData(emissions);
  };

  useEffect(() => {
    console.log('emissionsData', emissionsData)
  }, [emissionsData])

  return (
    <>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position='static'>
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>Compare Public Transport and Private Transport Carbon Emissions</Typography>
          </Toolbar>
        </AppBar>
        <div className="App">
          <Form getEmissionsData={getEmissionsData} />
          {emissionsData && <EmissionsResults emissionsData={emissionsData} buttonOnClick={handleOpenModal} />}
        </div>
      </Box>
      <Modal
        open={isModalOpen}
        onClose={handleCloseModal}
        aria-labelledby="learn-more-modal"
        aria-describedby="learn-more-about-public-vs-private-transport-carbon-emissions-modal"
        disableEnforceFocus
      >
        <Box sx={modalStyle}>
        <Typography id="modal-modal-title" variant="h5" component="h2">
            The Carbon Footprint of Transportation
          </Typography>
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            As concern about climate change has grown, individuals are becoming increasingly conscious of their impact on the environment.
            <br /><br />
            Transportation emissions often make up the largest portion of our individual carbon footprints. For that reason, evaluating transport options is a natural place to start, whether it is for a daily commute or a leisure trip abroad.
            <br /><br />
            So, what’s the most eco-friendly way to go from one place to another?
            <br /><br />
            The <a href='https://www.visualcapitalist.com/wp-content/uploads/2022/02/carbon-cost-of-transportation-ds.jpg'>infographic</a> charts the carbon footprint of transportation per passenger-kilometer for different vehicles based on data from the UK Government’s methodology paper for greenhouse gas reporting.
          </Typography>
        </Box>
      </Modal>
    </>
  );
}

export default App;
